DROP TABLE IF EXISTS `salary_cap_constants`;
CREATE TABLE `salary_cap_constants` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `year` int(11) NOT NULL,
  `team` int(11) NOT NULL,
  `carryover_incentive_adjustments` double NOT NULL,
  `adjusted_cap` double NOT NULL,
  `salary_cap` double NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`),
  KEY `fk_salary_cap_projections_team_idx` (`team`),
  CONSTRAINT `fk_salary_cap_projections_team` FOREIGN KEY (`team`) REFERENCES `team` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=319 DEFAULT CHARSET=utf8;

